import React, { useState, useContext } from 'react'
import WeatherContext from '../context/WeatherContext'

const Daily = (props) => {
    const { forecast } = useContext(WeatherContext)


    // creating an empty array to store date and min ,max temps
    const result = []
    console.log(result)
    const findMinMax = () => {
        forecast.list.forEach(l => {
            const date = new Date(l.dt_txt.split(' ')[0]).toDateString();
            const index = result.findIndex(item => item.date === date)
            if (index === -1) {
                result.push({
                    date: date,
                    min: l.main.temp_min,
                    max: l.main.temp_max
                });

            }
            else {
                if (l.main.temp_min < result[index].min) {
                    result[index].min = l.main.temp_min;
                }
                if (l.main.temp_max < result[index].max) {
                    result[index].max = l.main.temp_max;
                }

            }
        })

        console.log(result)
        return result;
    }

    findMinMax();

    const dailyReport = result.map((r) => {
        return (

            <div className="weatherDetails">
                <div className='date-temp'>
                    <h4>{r.date}</h4>

                    <h3>{Math.round(r.max)}°F</h3>
                    <h3>{Math.round(r.min)}°F</h3>
                </div>

            </div>
        )

    })

    return (
        < div >
            {dailyReport}
        </div >
    )


}

export default Daily
