import './App.css';

import Search from './components/Search'
import Nav from './components/Nav';
import Today from './components/Today'

import { useState, useEffect } from 'react'

function App() {
  const [weather, setWeather] = useState([])
  const apiKey = "77d6cae8ed4f306976db5aa9ea844e15"
  const getWeather = async (searchTerm) => {

    const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${searchTerm} &appid=${apiKey}`)
    const data = await response.json();
    console.log(data)
    setWeather(data)
  }

  useEffect(() => { getWeather(); }, [])
  return (
    <div className="App">
      <Search weatherSearch={getWeather} />
      <Today weather={weather} />
      <Nav />
    </div>
  );
}

export default App;
