import React from 'react'

const Today = (props) => {
    const {
        name
    } = props.weather

    const loaded = () => {

        return (

            <div>
                <div className="mainBox">
                    {/* <h2>{props.weather.city.name},{props.weather.city.country}</h2> */}
                    {/* <h3>{props.weather.name}</h3> */}
                    <h3>{name}</h3>
                    <div className="today-info">
                        <div>
                            <h3>{props.weather.main.temp}</h3>
                            <h4>{props.weather.weather[0].main}</h4>
                            <p>Max {props.weather.main.temp_max}  Min {props.weather.main.temp_min}</p>
                        </div>
                        <div>
                            <img src="https://i.pinimg.com/736x/36/bf/f0/36bff0ab1300f5409722973b82ef9c96--sunshine.jpg" alt="" className='image' />
                            <p>Feels like {props.weather.main.feels_like}  </p>
                        </div>
                    </div>




                </div>

            </div>



        );
    }
    const loading = () => {
        return <h1>No weather to display</h1>
    }
    return (
        props.weather ? loaded() : loading()

    )
}

export default Today
