import React from 'react'

const Daily = (props) => {
    console.log(props.forecast.list)



    const dailyReport1 = props.forecast.list.map((l, id) => {
        const dateString = l.dt_txt;
        const dateObject = new Date(dateString);
        const dayOfWeek = dateObject.toLocaleDateString('en-US', { weekday: 'long' }); // Get the day of the week as a string
        console.log(dayOfWeek)
        return (
            <div>
                <h4>{dayOfWeek}</h4>

                <h3>{l.main.temp_max}°F</h3>
                <h3>{l.main.temp_min}°F</h3>

            </div>
        )

    })

    const dailyReport = props.forecast.list.reduce((prev, current) => {
        const prevDay = prev.dt_txt;
        const prevdateObject = new Date(prevDay);
        const prevdayOfWeek = prevdateObject.toLocaleDateString('en-US', { weekday: 'long' }); // Get the day of the week as a string
        console.log(prevdayOfWeek)
        const currentDay = current.dt_txt;
        const currentdateObject = new Date(currentDay);
        const currentdayOfWeek = currentdateObject.toLocaleDateString('en-US', { weekday: 'long' }); // Get the day of the week as a string
        console.log(currentdayOfWeek)

        return (prevdayOfWeek === currentdayOfWeek && prev.main.temp_max > current.main.temp_max) ? prev : current;



    })
    console.log(dailyReport)
    return (

        < div >
            <p>{dailyReport.dt_txt}</p>
            <p>{dailyReport.main.temp_max}</p>
            {dailyReport1}
        </div >
    )
}

export default Daily
