import React from 'react'

const Today = (props) => {
    const {
        name,
        main: { temp, temp_min, temp_max, feels_like, humidity },
        dt,
        sys: { country },
        weather,

    } = props.weather

    const loaded = () => {

        return (

            <div>
                <div className="mainBox">

                    <h3>{name},{country}</h3>
                    <div className="today-info">
                        <div>
                            <h3>{temp}째F</h3>
                            <h4>{weather[0].main}</h4>
                            <p>Max {temp_max}째F  Min {temp_min}째F</p>
                        </div>
                        <div>
                            <img src="https://i.pinimg.com/736x/36/bf/f0/36bff0ab1300f5409722973b82ef9c96--sunshine.jpg" alt="" className='image' />
                            <p>Feels like {feels_like}째F  </p>
                            <p>Humidity {humidity}</p>
                        </div>
                    </div>




                </div>

            </div>



        );
    }
    const loading = () => {
        return <h1>No weather to display</h1>
    }
    return (
        props.weather ? loaded() : loading()

    )
}

export default Today
