import React from 'react'

const Today = (props) => {
    const {
        main: { temp, feels_like, temp_min, temp_max },
        name,
        dt,
        weather,
        sys: country
    } = props.weather
    const loaded = () => {

        return (

            <div>
                <div className="mainBox">

                    <h3>{name}</h3>
                    <div className="today-info">
                        <div>
                            <h3>{temp}</h3>
                            <h4>{weather[0].main}</h4>
                            <p>Max {temp_max}  Min {temp_min}</p>
                        </div>
                        <div>
                            <img src="https://i.pinimg.com/736x/36/bf/f0/36bff0ab1300f5409722973b82ef9c96--sunshine.jpg" alt="" className='image' />
                            <p>Feels like {feels_like}  </p>
                        </div>
                    </div>




                </div>

            </div>



        );
    }
    const loading = () => {
        return <h1>No weather to display</h1>
    }
    return (
        props.weather ? loaded() : loading()

    )
}

export default Today
