import React, { useState, useContext } from 'react'
import WeatherContext from '../context/WeatherContext'

const Example = (props) => {
    const { forecast } = useContext(WeatherContext)
    console.log(forecast.list)
    let date = ""
    forecast.list.forEach(l => {

        l.dt_txt = new Date(l.dt_txt.split(' ')[0]).toDateString();
        console.log(l.dt_txt)
    })
    console.log(forecast.list)
    date = forecast.list[0].dt_txt
    let min = forecast.list[0].main.temp_min
    let max = forecast.list[0].main.temp_max
    let i = 0;
    let j = 0;
    const arr1 = Array(5).fill().map(() => Array(3).fill())
    const findMinMax = () => {

        i = 0;
        j = 0;

        forecast.list.forEach(l => {
            if (date === l.dt_txt) {
                arr1[i][j] = l.dt_txt
                if (l.main.temp_min < min) {
                    min = l.main.temp_min
                    arr1[i][j + 1] = min

                }
                if (l.main.temp_max > max) {
                    max = l.main.temp_max
                    arr1[i][j + 2] = max
                }

            }
            else {
                date = l.dt_txt
                min = l.main.temp_min
                max = l.main.temp_max
                i++;
                j = 0;
                arr1[i][j] = l.dt_txt
                arr1[i][j + 1] = min
                arr1[i][j + 2] = max
            }

        })
        console.log(arr1)
    }

    findMinMax();

    //mapping through the list array and destructuring it
    const dailyReport = arr1.map((a) => {



        return (

            <div className="weatherDetails">
                <div className='date-temp'>
                    <h4>{date}</h4>

                    <h3>{Math.round(max)}°F</h3>
                    <h3>{Math.round(min)}°F</h3>
                </div>

            </div>
        )

    })

    return (
        < div >
            {dailyReport}
        </div >
    )


}

export default Example
