import './App.css';

import Search from './components/Search'

import Today from './components/Today'
import Button from './components/Button';
import Daily from './components/Daily';
import Weekend from './components/Weekend';

import { useState, useEffect } from 'react'
import Hourly from './components/Hourly';

function App() {
  //defining a Array variable to store weather data
  const [weather, setWeather] = useState({})
  const apiKey = "77d6cae8ed4f306976db5aa9ea844e15"
  //function to fetch data from weather using api and store it in the variable weather
  const getWeather = async (searchTerm) => {
    try {
      const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${searchTerm}&appid=${apiKey}&units=imperial`)
      const data = await response.json();
      console.log(data)
      setWeather(data)
    }
    catch (e) {
      console.error(e)
    }

  }

  useEffect(() => { getWeather("london"); }, [])
  const [str, setStr] = useState('')
  const [forecast, setForecast] = useState({})
  //function to fetch daily forecast of the selected city using lat and lon values
  const getForecast = async (btnName) => {
    try {
      const response = await fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${weather.coord.lat}&lon=${weather.coord.lon}&appid=${apiKey}&units=imperial`)
      const forecastData = await response.json();
      console.log(forecastData)
      setForecast(forecastData)

      setStr(btnName)
    }
    catch (e) {
      console.error(e)
    }

  }
  console.log(forecast)

  return (
    <div className="App">
      <Search weatherSearch={getWeather} />
      {/* <Today weather={weather} /> */}

      <Button getForecast={getForecast} txt="Hourly" />
      <Button getForecast={getForecast} txt="Daily" />
      <Button getForecast={getForecast} txt="Weekend" />

      {str === "Hourly" ? (<Hourly />) : null}
      {str === "Daily" ? (<Daily forecast={forecast} />) : null}
      {str === "Weekend" ? (<Weekend forecast={forecast} />) : null}
    </div>
  );
}

export default App;
