import React, { useState } from 'react'


const Weekend = (props) => {
    const [day1, setDay1] = useState('')
    const [date, setDate] = useState('')
    //mapping through the list array and destructuring it
    const dailyReport = props.forecast.list.map(({ dt_txt, main: { temp_max, temp_min }, weather, wind: { speed } }, id) => {
        setDate(new Date(dt_txt.split(' ')[0]).toDateString());
        const day = date.split(' ')[0]
        if (!day1) {
            const day = date.split(' ')[0]
            setDay1(day)
        }
        else if (day1 === day) {
            setDate("")
        }
        console.log(day1)
        return (
            <div >
                {day === "Fri" || day === "Sat" || day === "Sun" ? (
                    <div className="weatherDetails">
                        <div className='date-temp'>
                            <h4>{date}</h4>
                            <h3>{Math.round(temp_max)}°F</h3>
                            <h3>{Math.round(temp_min)}°F</h3>
                        </div>
                        <div className='weather'>
                            <img src={`https://openweathermap.org/img/w/${weather[0].icon}.png`} alt="" className='image' />
                            <h3>{weather[0].main}</h3>
                        </div>
                        <div className='wind'>
                            <img src="https://uxwing.com/wp-content/themes/uxwing/download/weather/wind-icon.png" alt="" className='wind-image' />
                            <h3>{Math.round(speed)} </h3>
                        </div>
                    </div>
                ) : null}
            </div>

        );


    })


    console.log(dailyReport)
    return (

        < div >

            {dailyReport}

        </div >
    )
}


export default Weekend
