import React from 'react'

const Daily = (props) => {
    console.log(props.forecast.list)


    //mapping through the list array and destructuring it
    const dailyReport = props.forecast.list.map(({ dt_txt, main: { temp_max, temp_min }, weather }, id) => {
        const dateString = dt_txt;
        const dateObject = new Date(dateString);
        const dayOfWeek = dateObject.toLocaleDateString('en-US', { weekday: 'long' }); // Get the day of the week as a string
        console.log(dayOfWeek)
        return (
            <div>
                <h4>{dayOfWeek}</h4>

                <h3>{temp_max}°F</h3>
                <h3>{temp_min}°F</h3>
                <img src={`https://openweathermap.org/img/w/${weather[0].icon}.png`} alt="" className='image' />
                <p>{weather[0].main}</p>

            </div>
        )

    })


    console.log(dailyReport)
    return (

        < div >

            {dailyReport}

        </div >
    )
}

export default Daily
