import './App.css';

import Search from './components/Search'

import Today from './components/Today'
import Button from './components/Button';
import Daily from './components/Daily';
import Weekend from './components/Weekend';

import { useState, useEffect } from 'react'
import Hourly from './components/Hourly';

function App() {
  //defining a Array variable to store weather data
  const [weather, setWeather] = useState([])
  const apiKey = "77d6cae8ed4f306976db5aa9ea844e15"
  //function to fetch data from weather using api and store it in the variable weather
  const getWeather = async (searchTerm) => {

    const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${searchTerm}&appid=${apiKey}`)
    const data = await response.json();
    console.log(data)
    setWeather(data)
  }

  useEffect(() => { getWeather(London); }, [])
  const [str, setStr] = useState('')
  const getData = (str1) => {
    console.log(str1)
    setStr(str1)

  }
  return (
    <div className="App">
      <Search weatherSearch={getWeather} />
      <Today weather={weather} />
      {/* <Today /> */}
      <Button getData={getData} txt="Hourly" />
      <Button getData={getData} txt="Daily" />
      <Button getData={getData} txt="Weekend" />
      {/* <Nav /> */}
      {str === "Hourly" ? (<Hourly />) : null}
      {str === "Daily" ? (<Daily />) : null}
      {str === "Weekend" ? (<Weekend />) : null}
    </div>
  );
}

export default App;
